
public class Data_types {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//TYPES OF DATATYPES
		//PRIMITIVA DATA TYPES(NUMERIC TYPES)
		//Integral value-integer
		byte numb = 123;//8bit
		System.out.println("value of byte is "+numb);
		short nums = 12345;//16bit
		System.out.println("value of short is "+nums);
		int numi = 1234567890;//32bit
		System.out.println("value of int is "+numi);
		long numl = 1234567890;//64bit
		System.out.println("value of long is "+numl);
		//Integral value-floating point
		float numf = 12345.234589747f;//F
		System.out.println("value of float is "+numf);
		double numd = 1234567.345678901234567;//d //D
		System.out.println("value of double is "+numd);
		//Numeric type-character value
		char grade = 'E';
		System.out.println("value of char variable is "+grade);
		//PRIMITIVA TYPE(boolean type)
		boolean numbo = true;
		System.out.println("value of boolean is "+numbo);
	    //NON PRIMITIVE DATA TYPE
		String var = "Welcome";
		System.out.println("value of String variable is "+var);

	}

}
